import React, {useEffect, useState} from "react";
import {useLocation, useNavigate} from "react-router-dom";
import useAuth from "../../hooks/useAuth";
import axios from "../../api/axios";
import {Card, Typography} from "@material-tailwind/react";
import { Navbar, Footer, Carousel, Alert, Input, Button } from "../webcomponent";
import { CheckIcon, DocumentDuplicateIcon } from "@heroicons/react/24/outline";

import { useCopyToClipboard } from "usehooks-ts";

const Login = () => {

    const {auth, setAuth} = useAuth();

    // const navigate = useNavigate();
    // const location = useLocation();

    // const [errorMsg, setErrorMsg] = useState({State: false, Type: "", Message: ""});
    //
    // useEffect(() => {
    //     setValidEmail({State: "Valid", Message: ""});
    //     setValidPassword({State: "Valid", Message: ""});
    //     setErrorMsg({State: false, Type: "", Message: ""});
    // }, [email, password])
    //
    // useEffect(() => {
    //     if (errorMsg.State) {
    //         setValidEmail({State: "Invalid", Message: "Email"});
    //         setValidPassword({State: "Invalid", Message: "Password"});
    //     }
    // }, [errorMsg])
    useEffect(() => {
        const hash = window.location.hash; // Gets the string after '#'
        if (hash) {
            const params = new URLSearchParams(hash.substring(1)); // remove '#' and parse
            const data = {
                "token" : params.get('access_token'),
                "scope": params.get('scope'),
            };

            if (data.token) {
                console.log('Access Token:', data.token);
                console.log('Scope:', data.scope);

                setAuth(data);
            }
        }
    }, []);

    const handleSubmit = async (e) => {

        // if (email === '' || password === '') {
        //     setErrorMsg({State: true, Type: "Error", Message: 'Username and Password are required'});
        //     return;
        // }

        e.preventDefault();

        try {
            const client_id = process.env.REACT_APP_IS_CLIENT_ID;
            const redirect_uri = process.env.REACT_APP_IS_REDIRECT_URI;
            const scope = process.env.REACT_APP_IS_SCOPE;
            window.open(`https://is.books.com/oauth2/authorize?response_type=token&client_id=${client_id}&redirect_uri=${redirect_uri}&scope=${scope}`, "_self");
            // let response = await axios.post('/auth/authenticate',
            //     JSON.stringify({email, password}),
            //     {
            //         headers: {'Content-Type': 'application/json'},
            //         withCredentials: true,
            //
            //     }
            // );
            // console.log(response?.data)
            // response.data.role = response?.data?.role.replace("_", " ");
            // console.log(response?.data)
            // setAuth(response?.data);
            //
            // setEmail('');
            // setPassword('');
            //
            // let role;
            // if (response?.data?.role === "INDIVIDUAL INVESTOR" || response?.data?.role === "ENTERPRISE INVESTOR") {
            //     role = "investor";
            // } else if (response?.data?.role === "CO ADMIN") {
            //     role = "admin";
            // } else {
            //     role = response?.data?.role.toLowerCase();
            // }
            //
            // const from = location?.state?.from || {pathname: "/" + role +"/dashboard"};
            // navigate(from, {replace: true});

        } catch (err) {
            // if (!err?.response) {
            //     setErrorMsg({State: true, Type: "Error", Message: 'No Server Response'});
            // } else if (err.response?.status === 403) {
            //     setErrorMsg({State: true, Type: "Error", Message: 'Invalid Username or Password'});
            // } else {
            //     setErrorMsg({State: true, Type: "Error", Message: err.response?.data?.message});
            // }
        }
    }

    function ClipboardCopyInput(props) {

        const string = props.string;

        const [value, copy] = useCopyToClipboard();
        const [copied, setCopied] = useState(false);
        const [inputValue, setInputValue] = useState(string);

        return (
            <div className="flex items-center gap-4">
                <div className="w-72">
                    <Input
                        type="text"
                        label="Access Token"
                        // onChange={(e) => setInputValue(e.target.value)}
                        value={inputValue || ''}
                    />
                </div>
                <Button
                    size="md"
                    onMouseLeave={() => setCopied(false)}
                    onClick={() => {
                        copy(inputValue);
                        setCopied(true);
                    }}
                    className="flex items-center gap-2 hover:!bg-main-purple hover:!text-white"
                >
                    {copied ? (
                        <>
                            <CheckIcon className="h-4 w-4 text-white" />
                            Copied
                        </>
                    ) : (
                        <>
                            <DocumentDuplicateIcon className="h-4 w-4 text-white" />
                            Copy
                        </>
                    )}
                </Button>
            </div>
        );
    }

    return (

        <div className="flex flex-col justify-between items-center w-full overflow-hidden">

            <Navbar active="Login"/>

            <section className="flex flex-col md:flex-row  md:space-y-0 md:space-x-16 justify-items-center md:mx-0 m-20 lg:my-[5rem] h-[60vh] items-center">
                <div className="my-130">
                    <Card color="transparent" className="items-center" shadow={false}>
                        <Typography variant="h1" className="text-[6rem] text-700 text-main-purple">
                            books.com
                        </Typography>
                        {
                            auth?.token ?
                                <>
                                    <ClipboardCopyInput string={auth.token}/>
                                    <Input
                                        type="text"
                                        label="Scope"
                                        // onChange={(e) => setInputValue(e.target.value)}
                                        value={auth?.scope || ''}
                                        className={"mt-4"}
                                    />
                                </> :
                                <>
                                    <form className="mt-2 mb-2 w-80 max-w-screen-lg sm:w-96" onSubmit={handleSubmit}>
                                        {/*<Alert Type={errorMsg.Type} Message={errorMsg.Message} State={errorMsg.State} />*/}
                                        <div className="flex flex-col gap-[0.75rem] items-center">
                                            <Button
                                                type="submit"
                                                label="Login"
                                                className="w-full rounded-[0rem]"
                                            />
                                        </div>
                                    </form>
                                </>
                        }

                    </Card>
                </div>
            </section>

            <Footer/>

        </div>

    )

}

export default Login
