import React, {useEffect, useState} from "react";
import {Button, Footer, Navbar} from "../webcomponent";
import {Avatar, Card, CardBody, CardHeader, IconButton, Progress, Typography,} from "@material-tailwind/react";
import {useNavigate} from "react-router-dom";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faChevronLeft, faChevronRight, faStar} from "@fortawesome/free-solid-svg-icons";
import axios from "../../api/axios";
import { ArrowRightIcon, ArrowLeftIcon } from "@heroicons/react/24/outline";


const Home = () => {

    const [books, setBooks] = useState(null);
    const [page, setPage] = useState(1);
    const [fetch, setFetch] = useState(5);

    useEffect(() => {
        const getDetails = async () => {

            try {
                let response = await axios.get(`/books?page=${page}&fetch=${fetch}`,
                    {
                        headers: {'Content-Type': 'application/json'},
                        // withCredentials: true,
                    }
                );
                console.log(response.data);
                setBooks(response.data);
            } catch (error) {
                console.log(error);
            }

        }

        getDetails().then();

    }, [page]);

    const navigate  = useNavigate() ;

    function DefaultPagination() {
        const [active, setActive] = useState(books?.meta?.current_page);
        const [numbers, setNumbers] = useState([]);

        useEffect(() => {
            let listItems = []
            for (let i = 1; i < books?.meta?.total_pages + 1; i++) {
                listItems.push(
                    <IconButton
                        key={i}
                        variant={active === i ? "filled" : "text"}
                        className={active === i ? "bg-main-purple text-white" : "text-black"}
                        onClick={() => {
                            setActive(i)
                            setPage(i)
                        }}
                    >
                        {i}
                    </IconButton>
                );
            }
            setNumbers(listItems)
        }, [active]);

        const next = () => {
            if (active === books?.meta?.total_pages) return;
            setPage(active + 1);
            setActive(active + 1);
        };

        const prev = () => {
            if (active === 1) return;
            setPage(active + 1);
            setActive(active - 1);
        };

        return (
            <div className="flex items-center gap-4">
                <Button
                    variant="text"
                    className="flex items-center gap-2"
                    onClick={prev}
                    disabled={active === 1}
                >
                    <ArrowLeftIcon strokeWidth={2} className="h-4 w-4" /> Previous
                </Button>
                <div className="flex items-center gap-2">
                    {numbers}
                </div>
                <Button
                    variant="text"
                    className="flex items-center gap-2"
                    onClick={next}
                    disabled={active === books?.meta?.total_pages}
                >
                    Next
                    <ArrowRightIcon strokeWidth={2} className="h-4 w-4" />
                </Button>
            </div>
        );
    }

    return (

        <div className="flex flex-col justify-between items-center w-full overflow-hidden">

            <Navbar active="Home"/>

            <DefaultPagination/>

            <Footer/>

        </div>

    )

}

export default Home
