    //                 }
    //             );
    //             console.log(response);
    //             setList(response.data);
    //         } catch (error) {
    //             console.log(error);
    //         }
    //
    //     }
    //
    //     getDetails().then();
    //
    // }, [page]);

    const [email, setEmail] = useState('');
    const [validEmail, setValidEmail] = useState({"State": "", "Message": ""});

    const [book, setBook] = useState('');
    const [validPassword, setValidPassword] = useState({"State": "", "Message": ""});

    const [errorMsg, setErrorMsg] = useState({State: false, Type: "", Message: ""});

    const handleSubmit = (e) => {}

    return (

        <div className="flex flex-col justify-between items-center w-full overflow-hidden">

            <Navbar active="Home"/>

            <section className="flex flex-col md:flex-row  md:space-y-0 md:space-x-16 justify-items-center md:mx-0 m-20 lg:my-[5rem] h-[60vh] items-center">
                <div className="my-130">
                    <form className="mt-2 mb-2 w-80 max-w-screen-lg sm:w-96" onSubmit={handleSubmit}>
                        <Alert Type={errorMsg.Type} Message={errorMsg.Message} State={errorMsg.State} />
                        <div className="flex flex-col gap-[0.75rem] items-center">
                            <Select
                                label="District"
                                value={formData.district}
                                options={["Ampara", "Anuradhapura", "Badulla", "Batticaloa", "Colombo", "Galle", "Gampaha", "Hambantota", "Jaffna", "Kalutara", "Kandy", "Kegalle", "Kilinochchi", "Kurunegala", "Mannar", "Matale", "Matara", "Monaragala", "Mullaitivu", "Nuwara Eliya", "Polonnaruwa", "Puttalam", "Ratnapura", "Trincomalee", "Vavuniya"]}
                                onChange={(event)=>
                                    setBook(event)
                                }
                                required={true}
                            />
                            <Input
                                type="password"
                                label="Password"
                                onChange={(e) => setPassword(e.target.value)}
                                value={password}
                                state={validPassword}
                                required
                            />
                            <Button
                                type="submit"
                                label="Login"
                                className="w-full"
                            />
                        </div>
                    </form>
                </div>
            </section>

            <Footer/>

        </div>

    )

}

export default Purchases
