import React, {useEffect, useState} from "react";
import {Button, Footer, Navbar} from "../webcomponent";
import {
    Avatar,
    Card,
    CardBody,
    CardFooter,
    CardHeader,
    IconButton,
    Progress,
    Typography,
} from "@material-tailwind/react";
import {useNavigate} from "react-router-dom";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faChevronLeft, faChevronRight, faStar} from "@fortawesome/free-solid-svg-icons";
import axios from "../../api/axios";
import { ArrowRightIcon, ArrowLeftIcon } from "@heroicons/react/24/outline";


const Home = () => {

    const [list, setList] = useState(null);
    const [page, setPage] = useState(1);
    const [fetch, setFetch] = useState(5);

    useEffect(() => {
        const getDetails = async () => {

            try {
                let response = await axios.get(`/books?page=${page}&fetch=${fetch}`,
                    {
                        headers: {'Content-Type': 'application/json'},
                        // withCredentials: true,
                    }
                );
                console.log(response.data);
                setList(response.data);
            } catch (error) {
                console.log(error);
            }

        }

        getDetails().then();

    }, [page]);

    const navigate  = useNavigate() ;

    function DefaultPagination() {
        const [active, setActive] = useState(list?.meta?.current_page);
        const [numbers, setNumbers] = useState([]);

        useEffect(() => {
            let listItems = []
            for (let i = 1; i < list?.meta?.total_pages + 1; i++) {
                listItems.push(
                    <IconButton
                        key={i}
                        variant={active === i ? "filled" : "text"}
                        className={active === i ? "bg-main-purple text-white" : "text-black"}
                        onClick={() => {
                            setActive(i)
                            setPage(i)
                        }}
                    >
                        {i}
                    </IconButton>
                );
            }
            setNumbers(listItems)
        }, [active]);

        const next = () => {
            if (active === list?.meta?.total_pages) return;
            setPage(active + 1);
            setActive(active + 1);
        };

        const prev = () => {
            if (active === 1) return;
            setPage(active + 1);
            setActive(active - 1);
        };

        return (
            <div className="flex items-center gap-4">
                <Button
                    variant="text"
                    className="flex items-center gap-2"
                    onClick={prev}
                    disabled={active === 1}
                >
                    <ArrowLeftIcon strokeWidth={2} className="h-4 w-4" /> Previous
                </Button>
                <div className="flex items-center gap-2">
                    {numbers}
                </div>
                <Button
                    variant="text"
                    className="flex items-center gap-2"
                    onClick={next}
                    disabled={active === list?.meta?.total_pages}
                >
                    Next
                    <ArrowRightIcon strokeWidth={2} className="h-4 w-4" />
                </Button>
            </div>
        );
    }

    return (

        <div className="flex flex-col justify-between items-center w-full overflow-hidden">

            <Navbar active="Home"/>

            {
                list?.Books.map((book, index) => (
                    <Card className="mt-6 w-96" key={index}>
                        <CardBody>
                            <svg
                                xmlns="http://www.w3.org/2000/svg"
                                viewBox="0 0 24 24"
                                fill="currentColor"
                                className="mb-4 h-12 w-12 text-gray-900"
                            >
                                <path
                                    fillRule="evenodd"
                                    d="M9.315 7.584C12.195 3.883 16.695 1.5 21.75 1.5a.75.75 0 01.75.75c0 5.056-2.383 9.555-6.084 12.436A6.75 6.75 0 019.75 22.5a.75.75 0 01-.75-.75v-4.131A15.838 15.838 0 016.382 15H2.25a.75.75 0 01-.75-.75 6.75 6.75 0 017.815-6.666zM15 6.75a2.25 2.25 0 100 4.5 2.25 2.25 0 000-4.5z"
                                    clipRule="evenodd"
                                />
                                <path d="M5.26 17.242a.75.75 0 10-.897-1.203 5.243 5.243 0 00-2.05 5.022.75.75 0 00.625.627 5.243 5.243 0 005.022-2.051.75.75 0 10-1.202-.897 3.744 3.744 0 01-3.008 1.51c0-1.23.592-2.323 1.51-3.008z" />
                            </svg>
                            <Typography variant="h5" color="blue-gray" className="mb-2">
                                UI/UX Review Check
                            </Typography>
                            <Typography>
                                Because it&apos;s about motivating the doers. Because I&apos;m here to
                                follow my dreams and inspire others.
                            </Typography>
                        </CardBody>
                        <CardFooter className="pt-0">
                            <a href="#" className="inline-block">
                                <Button size="sm" variant="text" className="flex items-center gap-2">
                                    Learn More
                                    <svg
                                        xmlns="http://www.w3.org/2000/svg"
                                        fill="none"
                                        viewBox="0 0 24 24"
                                        strokeWidth={2}
                                        stroke="currentColor"
                                        className="h-4 w-4"
                                    >
                                        <path
                                            strokeLinecap="round"
                                            strokeLinejoin="round"
                                            d="M17.25 8.25L21 12m0 0l-3.75 3.75M21 12H3"
                                        />
                                    </svg>
                                </Button>
                            </a>
                        </CardFooter>
                    </Card>
                ))
            }

            <DefaultPagination/>

            <Footer/>

        </div>

    )

}

export default Home
