import React, {useEffect, useState} from "react";
import {Button, Footer, Navbar} from "../webcomponent";
import {
    Avatar,
    Card,
    CardBody,
    CardFooter,
    CardHeader,
    IconButton,
    Progress,
    Typography,
} from "@material-tailwind/react";
import {Link, useNavigate} from "react-router-dom";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faChevronLeft, faChevronRight, faStar} from "@fortawesome/free-solid-svg-icons";
import axios from "../../api/axios";
import { ArrowRightIcon, ArrowLeftIcon } from "@heroicons/react/24/outline";


const Home = () => {

    const [list, setList] = useState(null);
    const [page, setPage] = useState(1);
    const [fetch, setFetch] = useState(5);

    useEffect(() => {
        const getDetails = async () => {

            try {
                let response = await axios.get(`/books?page=${page}&fetch=${fetch}`,
                    {
                        headers: {'Content-Type': 'application/json'},
                        // withCredentials: true,
                    }
                );
                console.log(response.data);
                setList(response.data);
            } catch (error) {
                console.log(error);
            }

        }

        getDetails().then();

    }, [page]);

    const navigate  = useNavigate() ;

    function DefaultPagination() {
        const [active, setActive] = useState(list?.meta?.current_page);
        const [numbers, setNumbers] = useState([]);

        useEffect(() => {
            let listItems = []
            for (let i = 1; i < list?.meta?.total_pages + 1; i++) {
                listItems.push(
                    <IconButton
                        key={i}
                        variant={active === i ? "filled" : "text"}
                        className={active === i ? "bg-main-purple text-white" : "text-black"}
                        onClick={() => {
                            setActive(i)
                            setPage(i)
                        }}
                    >
                        {i}
                    </IconButton>
                );
            }
            setNumbers(listItems)
        }, [active]);

        const next = () => {
            if (active === list?.meta?.total_pages) return;
            setPage(active + 1);
            setActive(active + 1);
        };

        const prev = () => {
            if (active === 1) return;
            setPage(active + 1);
            setActive(active - 1);
        };

        return (
            <div className="flex items-center gap-4">
                <Button
                    variant="text"
                    className="flex items-center gap-2"
                    onClick={prev}
                    disabled={active === 1}
                >
                    <ArrowLeftIcon strokeWidth={2} className="h-4 w-4" /> Previous
                </Button>
                <div className="flex items-center gap-2">
                    {numbers}
                </div>
                <Button
                    variant="text"
                    className="flex items-center gap-2"
                    onClick={next}
                    disabled={active === list?.meta?.total_pages}
                >
                    Next
                    <ArrowRightIcon strokeWidth={2} className="h-4 w-4" />
                </Button>
            </div>
        );
    }

    return (

        <div className="flex flex-col justify-between items-center w-full overflow-hidden">

            <Navbar active="Home"/>

            <div className="flex flex-col justify-center">
                <div className="flex flex-wrap gap-4 justify-start mb-8">
                    {
                        list?.Books.map((book, index) => (
                            <Card className="w-96 shadow-none border-2" key={index}>
                                <CardBody>
                                    <Typography variant="small" color="black" className="mb-2 font-bold">
                                        {book.book_id}
                                    </Typography>
                                    <Typography variant="h5" color="blue-gray" className="mb-2">
                                        {book.title}
                                    </Typography>
                                    <Typography className={book.availability === "In Stock" ? "text-green-500 font-medium" : "text-error-red font-medium"}>
                                        {book.availability}
                                    </Typography>
                                </CardBody>
                                {/*<CardFooter className="pt-0">*/}
                                {/*    <Button*/}
                                {/*        variant="primary"*/}
                                {/*        label="Show more"*/}
                                {/*        icon="next"*/}
                                {/*    >*/}
                                {/*        <Link to={`/book/${book.book_id}`}>View</Link>*/}
                                {/*    </Button>*/}
                                {/*</CardFooter>*/}
                            </Card>
                        ))
                    }
                </div>

                <DefaultPagination/>
            </div>


            <Footer/>

        </div>

    )

}

export default Home
