import React, {useEffect, useState} from "react";
import {Button, Footer, Navbar} from "../webcomponent";
import {Avatar, Card, CardBody, CardHeader, IconButton, Progress, Typography,} from "@material-tailwind/react";
import {useNavigate} from "react-router-dom";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faChevronLeft, faChevronRight, faStar} from "@fortawesome/free-solid-svg-icons";
import axios from "../../api/axios";
import { ArrowRightIcon, ArrowLeftIcon } from "@heroicons/react/24/outline";


const Home = () => {

    const [books, setBooks] = useState(null);

    useEffect(() => {
        const getDetails = async () => {

            try {
                let response = await axios.get('/books?page=1&fetch=4',
                    {
                        headers: {'Content-Type': 'application/json'},
                        // withCredentials: true,

                    }
                );

                setBooks(response.data);
            } catch (error) {
                console.log(error);
            }

        }

        getDetails().then();

    }, []);

    const navigate  = useNavigate() ;

    function DefaultPagination() {
        const [active, setActive] = useState(books?.current_page);
        const [numbers, setNumbers] = useState([]);

        useEffect(() => {
            let listItems = []
            for (let i = 0; i < books?.total_pages; i++) {
                listItems.push(<IconButton {...getItemProps({i})}>{i}</IconButton>);
            }
            setNumbers(listItems)
        }, [])

        const getItemProps = (index) => ({
            variant: active === index ? "filled" : "text",
            color: "gray",
            onClick: () => setActive(index),
        });

        const next = () => {
            if (active === 5) return;

            setActive(active + 1);
        };

        const prev = () => {
            if (active === 1) return;

            setActive(active - 1);
        };

        return (
            <div className="flex items-center gap-4">
                <Button
                    variant="text"
                    className="flex items-center gap-2"
                    onClick={prev}
                    disabled={active === 1}
                >
                    <ArrowLeftIcon strokeWidth={2} className="h-4 w-4" /> Previous
                </Button>
                <div className="flex items-center gap-2">
                    {numbers}
                </div>
                <Button
                    variant="text"
                    className="flex items-center gap-2"
                    onClick={next}
                    disabled={active === 5}
                >
                    Next
                    <ArrowRightIcon strokeWidth={2} className="h-4 w-4" />
                </Button>
            </div>
        );
    }

    return (

        <div className="flex flex-col justify-between items-center w-full overflow-hidden">

            <Navbar active="Home"/>

            <DefaultPagination/>

            <Footer/>

        </div>

    )

}

export default Home
